class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=target.size();
        vector<int> diff(n,0);
        for(int i=0;i<n;i++)diff[i]=target[i]-nums[i];
        long long ans=0;
        long long temp=0;
        long long position=0;
        ans=abs(diff[0]);
        temp=ans;
        if(diff[0]>0)
            position=1;
        for(int i=1;i<n;i++){
            int temp2=abs(diff[i]);
            if(diff[i]<0 && position==1){
                ans+=temp2;
                temp=temp2;
                position=0;
                continue;
            }
            if(diff[i]>=0 && position==0){
                ans+=temp2;
                temp=temp2;
                position=1;
                continue;
            }
            if(temp>=temp2){
                temp=temp2;
                continue;
            }
            else{
                ans+=temp2-temp;
                temp=temp2;
            }
        }    
        return ans;
    }
};
