class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        int n = nums.size();
        for(int i=0; i<n; i++) {
            t[i] -= nums[i];
        }

        vector<int> d(n+1);
        d[0] = t[0];
        for(int i=1; i<n; i++) {
            d[i] = t[i] - t[i-1];
        }
        d[n] = -t[n-1];

        long long res = 0;
        queue<int> ne, po;
        for(int i=1; i<=n; i++) {
            if(d[i] < 0) {
                ne.push(i);
            }
            if(d[i] > 0) {
                po.push(i);
            }
        }

        for(int i=0; i<n; i++) {
            if(d[i] < 0) {
                while(d[i] < 0) {
                    int nxt = po.front();
                    if(d[nxt] + d[i] <= 0) {
                        res += abs(d[nxt]);
                        d[i] += d[nxt];
                        d[nxt] = 0;
                        po.pop();
                    } else {
                        res += abs(d[i]);
                        d[nxt] += d[i];
                        d[i] = 0;
                    }
                }
            }
            if(d[i] > 0) {
                while(d[i] > 0) {
                    int nxt = ne.front();
                    if(d[nxt] + d[i] >= 0) {                        
                        res += abs(d[nxt]);
                        d[i] += d[nxt];
                        d[nxt] = 0;
                        ne.pop();
                    } else {
                        res += abs(d[i]);
                        d[nxt] += d[i];
                        d[i] = 0;
                    }
                }
            }
        }
        return res;
    }
};
