class Solution {
public:
    #define ll long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;i++){
            nums[i]=target[i]-nums[i];
        }
        int i=0;
        int pmin=0,nmin=0;
        while(i<n){
            if(nums[i]>0){
                nmin=0;
                ll maxi=0;
                int prev=nums[i];

                while(i<n && nums[i]>=prev && nums[i]>=0){
                    prev=nums[i];
                    i++;
                }
                maxi=prev;
                while(i<n && nums[i]<=prev && nums[i]>=0){
                    prev=nums[i];
                    i++;
                }
                ans+=maxi-pmin;
                pmin=prev;
            }
            else if(nums[i]<0){
                pmin=0;
                ll maxi=0;
                int prev=abs(nums[i]);

                while(i<n && abs(nums[i])>=prev && nums[i]<=0){
                    prev=abs(nums[i]);
                    i++;
                }
                maxi=prev;
                while(i<n && abs(nums[i])<=prev && nums[i]<=0){
                    prev=abs(nums[i]);
                    i++;
                }
                ans+=maxi-nmin;
                nmin=prev;
            }
            else i++;
        }
        return ans;
        // [9,2,6,10,4,8,3,4,2,3]
        // [9,5,5,1,7,9,8,7,6,5]
        //  0 3 -1 -9 3 1 5 3 4 2
        //  [5,9,2,2]
// [7,9,3,8]

    }
};
