#define ll long long
#define pb push_back
#define ff first
#define ss second
#define mp make_pair

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n= a.size();
        ll an= 0, i= 0;
        vector<ll> c(n);
        for(i=0; i<n; i++)  c[i]= b[i]-a[i];
        // for(i=0; i<n; i++)  cout<<c[i]<<" ";
        // cout<<endl;
        i=0;
        vector<pair<ll, ll>> pos, neg;
        while(i<n){
            while(i<n && c[i]== 0)  i++;
            if(i<n){
                if(c[i]>0){
                    ll st= i, en;
                    while(i<n && c[i]>0)    en=i, i++;
                    pos.pb(mp(st, en));
                }
                else{
                    ll st= i, en;
                    while(i<n && c[i]<0)    en=i, i++;
                    neg.pb(mp(st, en));
                }
            }
        }
        for(auto p: pos){
            vector<ll> te;
            for(i= p.ff; i<= p.ss; i++)   te.pb(c[i]);
            an+= solve(te);
        }
        for(auto p: neg){
            vector<ll> te;
            for(i= p.ff; i<= p.ss; i++)   te.pb(-c[i]);
            an+= solve(te);
        }
        return an;
    }

    ll solve(vector<ll> &a){
        ll n= a.size(), i;
        if(n== 1)   return a[0];
        ll an= a[0];
        for(i=1; i<n; i++){
            if(a[i]<= a[i-1])   continue;
            an+= a[i]-a[i-1];
        }
        return an;
    }
};
