class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0;
        long long c1=target[0]-nums[0],c2,p=0;
        for(int i=0;i<nums.size();i++){
            c2=nums[i]-target[i];
            //-1 -1 -1 -2
            // c1=-1 r=1 c1=-1 r=1 ... c1=-1 c2=-2 r=2
            // -3 0 0 2
            // c1=-3 r=3 c1=0 r=3 c1=0 r=3 
            // 0 -3 1 9 -3 -1 -5 -3 -4 -2
            if((c1>=0 && c2<=0) || (c1<=0 && c2>=0) ){
                res+=abs(c2);
                //cout<<res<<"res"<<endl;
            }
            else{
                if(c2>c1 && c1>0) res+=abs(c1-c2);
                else if(c1>c2 && c1<0) res+=abs(c1-c2);
                //cout<<res<<"res"<<endl;
                //cout<<res<<" "<<c1<<" "c2<<endl;
            }
            //cout<<"c1: "<<c1<<" c2: "<<c2<<endl;
            c1=c2;
            //cout<<c1<<endl;
        }
        return res;
    }
};
