class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        vector<int> d(a.size());
        for (size_t i = 0; i < a.size(); ++i) {
            d[i] = b[i] - a[i];
        }
        long long ops = 0;
        int currDiff = 0;

        for (size_t i = 0; i < d.size(); ++i) {
            if (i == 0 || (d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0)) {
                int addOps = abs(d[i]) - abs(currDiff);
                if (addOps > 0) {
                    ops += addOps;
                }
                currDiff = d[i];
            } else {
                ops += abs(d[i]);
                currDiff = d[i];
            }
        }
        return ops;
    }
};
