class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0; int n=nums.size();
        vector<int>  diff(n);
       for(int i=0;i<n;i++){
        diff[i]= target[i]-nums[i];
       }
     int r=0;
     while(r<n){

        if(diff[r]==0){
            r++;
            continue;
        }
        else if(diff[r]>0){
            int maxi=INT_MAX,l=r;
            while(  r<n && diff[r] >0) { maxi = min(maxi,diff[r]); r++; }
            for(int i=l;i<r;i++) diff[i] -= maxi;
            ans+= maxi; r=l;
        }
        else{
            int maxi=INT_MAX,l=r;
            while( r<n && diff[r]<0) {maxi= min(maxi,abs(diff[r])); r++;}
            for(int i=l;i<r;i++){ diff[i] += maxi;}
            ans+= maxi; r=l;
        }
     }
       return ans;
    }
};
