class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [a-b for a, b in zip(nums, target)]
        ans = 0
        curr = 0
        for i in range(len(diff)):
            if diff[i] == 0:
                curr = 0
                continue
            if diff[i] > 0:
                if curr < 0:
                    curr = 0
                if curr < diff[i]:
                    ans += diff[i]-curr
                curr = diff[i]
            else:
                if curr > 0:
                    curr = 0
                if curr > diff[i]:
                    ans += curr-diff[i]
                curr = diff[i]
        return ans
