class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long total = 0;

        int canAdd = 0;
        int preSign = 0;

        for(int i = 0; i < n; i++){
            int diff = target[i] - nums[i];
            int absDiff = abs(diff);
            int currSign = diff == 0 ? 0 : diff / absDiff;

            // cout << diff << "";
            if(currSign == preSign){
                if(absDiff > canAdd){
                    total += absDiff - canAdd;
                    canAdd = absDiff;
                }else{
                    canAdd = absDiff;
                }
            }else{
                canAdd = absDiff;
                total += canAdd;
            }

            // cout << ":" << total << " ";
            preSign = currSign;
        }

        return total;
    }
};
