class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0, x = 0, mn, mx;
        vector<long long> vec(n, 0);
        for(int i = 0; i < n; i++){
            vec[i] = target[i] - nums[i];
        }
        for(int i = 0; i < n; i++){
            while(vec[i]){
                if(vec[i]>0){
                    mn = INT_MAX;
                    for(int j = i; j < n && vec[j]>0; j++){
                        mn = min(mn, vec[j]);
                    }
                    ans += mn;
                    for(int j = i; j < n && vec[j]>0; j++){
                        vec[j] -= mn;
                    }
                }else{
                    mx = INT_MIN;
                    for(int j = i; j < n && vec[j]<0; j++){
                        mx = max(mx, vec[j]);
                    }
                    ans -= mx;
                    for(int j = i; j < n && vec[j]<0; j++){
                        vec[j] -= mx;
                    }
                }
            }
        }
        return ans;
    }
};
