class Solution {
#define ll long long
private:

ll f(vector<ll> v) {
    ll n = v.size();
    for(int i = 0; i < n; i++) v[i] = abs(v[i]);

    ll ans = v[0];
    ll canReuse = v[0];

    for(int i = 1; i < n; i++) {
        if(v[i] <= canReuse) {
            canReuse = v[i];
        } else {
            ans += v[i] - canReuse;
            canReuse = v[i];
        }
    }

    return ans;
}

public:
long long minimumOperations(vector<int>& nums, vector<int>& target) {
    int n = nums.size();
    vector<ll> v(n);

    for(int i = 0; i < n; i++) {
        v[i] = target[i] - nums[i];
    }

    // for(auto x : v) cout << x << " ";
    // cout << endl;

    int i = 0;
    vector<ll> arr;
    vector<vector<ll>> neg;

    while(i < n) {
        int j = i;
        while(j < n) {
            vector<ll> temp;
            while(j < n && ((v[i] >= 0 && v[j] >= 0) || (v[i] < 0 && v[j] < 0))) {
                temp.push_back(v[j]);
                j++;
            }
            if(v[i] >= 0) {
                for(int k = 0; k < temp.size(); k++) {
                    arr.push_back(temp[k]);
                }
            } else {
                for(int k = 0; k < temp.size(); k++) {
                    arr.push_back(0);
                }
                neg.push_back(temp);
            }
            i = j;
        }
    }

    ll ans = f(arr);

    for(auto x : neg) {
        ll val = f(x);
        ans += val;
    }

    return ans;


