class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int i=0;
        long long prevdiff=0;
        long long maxdiff=0;
        while(i<nums.size()){
            long long diff=target[i]-nums[i];
            if(diff==0){
                ans=ans+abs(maxdiff);
                maxdiff=0;
                prevdiff=0;
                i++;
            }
            else if((prevdiff>=0 && diff<0) || (prevdiff<=0 && diff>0)){
                ans=ans+abs(maxdiff);
                maxdiff=0;
                prevdiff=diff;
            }
            else if(prevdiff>0 && diff>0){
                if(diff>maxdiff){
                    maxdiff=diff;
                }
                else{
                    ans=ans+maxdiff-diff;
                    maxdiff=diff;
                }
                prevdiff=diff;
                i++;
            }
            else if(prevdiff<0 && diff<0){
                if(abs(diff)>abs(maxdiff)){
                    maxdiff=diff;
                }
                else{
                    ans=ans+abs(maxdiff-diff);
                    maxdiff=diff;
                }
                prevdiff=diff;
                i++;
            }
        }
        ans=ans+abs(maxdiff);

        return ans;
    }
};
