class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int sign = 0, diff = 0, n = nums.length;
        long ans = 0;
        int i = 0;
        while (i < n) {
            if (sign == 0) {
                if (nums[i] < target[i]) {
                    sign = 1;
                    diff = target[i] - nums[i];
                } else if (nums[i] > target[i]) {
                    sign = -1;
                    diff = nums[i] - target[i];
                }
            } else {
                if ((nums[i] >= target[i] || sign != 1 || diff != target[i] - nums[i]) && (nums[i] <= target[i] || sign != -1 || diff != nums[i] - target[i])) {
                    if (nums[i] < target[i] && sign == 1) {
                        if (target[i] - nums[i] <= diff) ans += (diff - target[i] + nums[i]);
                        diff = target[i] - nums[i];
                    } else if (nums[i] > target[i] && sign == -1) {
                        if (nums[i] - target[i] <= diff) ans += (diff + target[i] - nums[i]);
                        diff = nums[i] - target[i];
                    } else if (nums[i] == target[i]) {
                        ans += diff;
                        sign = 0;
                        diff = 0;
                    } else {
                        ans += diff;
                        if (nums[i] < target[i]) {
                            sign = 1;
                            diff = target[i] - nums[i];
                        } else {
                            sign = -1;
                            diff = nums[i] - target[i];
                        }
                    }
                }
            }
            i++;
        }
        return ans + diff;
    }
}
