class Solution {
public:
    bool sameSign(int n){
        return (n>=0);
    }
    long long minNumberOperations(vector<int>& target) {
        long long noOfOperations = target[0];
        for(int i = 1; i < target. size(); i++) {
            if(target[i] > target[i-1]) 
                noOfOperations += (target[i]-target[i-1]);
        }
        return noOfOperations;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<n;i++){
            target[i]-=nums[i];
        }
        int i=0;
        long long ans=0;
        while(i<n){
            vector<int> tmp;
            int st=target[i];
            while(i<n && sameSign(target[i])==sameSign(st)){
                tmp.push_back(abs(target[i]));
                i++;
            }
            ans+=minNumberOperations(tmp);

        }
        return ans;
    }
};
