class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < nums.size(); i++) {
            nums[i] -= target[i];
        }
        long long m = abs(nums[0]);
        bool na = nums[0] >= 0 ? false:true;
        long long ret = 0;
        int a = 0;
        int n = nums[0];
        int mm = 0;
        for (int i = 1; i < nums.size(); i++) {
            int nn = nums[i];
            if (nn > 0 && !na) {
                if (nn > n && a == 1) {
                    ret += (m - mm);
                    //cout << (m - mm) << endl;
                    m = nn;
                    mm = n;
                    n = nn;
                    continue;
                } else if (n > 0 && nn < n && a == 0) {
                    a = 1;
                    n = nn;
                    continue;
                }
                m = m > nn ? m:nn;
            } else if (nn < 0 && na) {
                if (abs(nn) > abs(n) && a == 1) {
                    ret += (m - mm);
                    //cout << (m - mm) << endl;
                    m = abs(nn);
                    mm = abs(n);
                    //cout << m << endl;
                    //cout << mm << endl;
                    n = nn;
                    continue;
                } else if (n < 0 && abs(nn) < abs(n) && a == 0) {
                    a = 1;
                    n = nn;
                    continue;
                }
                m = m > abs(nn) ? m : abs(nn);
            } else {
                ret += (m - mm);
                //cout << (m - mm) << endl;
                m = abs(nn);
                a = 0;
                na = nn >= 0 ? false:true;
                mm = 0;
            }
            n = nn;
        }
        ret += (m - mm);
        //cout << (m - mm) << endl;
        return ret;
    }
};
