class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long prev = 0;
        int m = nums.size();
        if(m==1)return abs(target[0] - nums[0]);
        prev = abs(target[0] - nums[0]);
        for(int i=1; i<m; i++){
            int v1 = nums[i];
            int v2 = target[i];
            int p1 = nums[i-1];
            int p2 = target[i-1];
            int r1 = v1-v2;
            int r2 = p1-p2;
            if(r1==0)continue;
            if(r1==r2)continue;
            if((r1>=0&&r2<=0)||(r1<=0&&r2>=0))prev += abs(r1);
            else if(abs(r1)>abs(r2))prev += abs(r1-r2);    
        }
         return prev;
    }
};
