class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long total = 0;
        int last = 0;

        for (int i = 0; i < nums.size(); ++i) {
            int diff = target[i] - nums[i];
            // printf("%d ", diff);

            if (last > 0 && diff > 0) {
                total += max(diff - last, 0);
            } else if (last < 0 && diff < 0) {
                total += max(last - diff, 0);
            } else {
                // last & diff has different sign
                // last = 0
                total += abs(diff);
            }
            last = diff;
            // printf("=> %d %d\n", last, total);
        }

        return total;
    }
};
