class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n=nums.length;
        long diff[]=new long[n];
        for (int i=0;i<n;i++) {
            diff[i]=target[i]-nums[i];
        }
        long res = 0,curDiff = 0;
        for (int i=0;i<n;i++) {
            if (i==0||(diff[i-1]>0&&diff[i]>0)||(diff[i-1]<0&&diff[i]<0)) {
                long add=Math.abs(diff[i])-Math.abs(curDiff);
                if (add>0){
                    res+=add;
                }
                curDiff=diff[i];
            } else {
                res+=Math.abs(diff[i]);
                curDiff=diff[i];
            }
        }

        return res;
    }
}
