class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [target[i] - nums[i] for i in range(n)]
        # print(diff)
        def minstep(diffs):
            # 看[i，j](inluded) 内部需要的步数
            # assume 内部都是正数
            if not diffs:
                return 0
            if len(diffs) == 1:
                return diffs[0]
            cnt = 0
            level = min(diffs)
            cnt += level
            last = -1
            for i,c in enumerate(diffs):
                diffs[i]-=level
                if diffs[i]==0:
                    if i-last>1:
                        cnt += minstep(diffs[last+1:i])
                    last = i 
            if last<n-1:
                cnt += minstep(diffs[last+1:n])
            return cnt
        sign = 0
        last = -1 
        cnt = 0
        for i, c in enumerate(diff):
            if c==0:
                cnt += minstep(diff[last+1: i])
                last = i
            if c>0:
                if sign == -1:
                    cnt += minstep(diff[last+1 : i])
                    # cur is not included
                    last = i - 1 
                sign = 1 
            if c<0:
                diff[i] = -diff[i] # all to positive
                if sign == 1:
                    cnt += minstep(diff[last+1 : i])
                    last = i -1 
                sign = -1 

            # print(i, c, cnt)
        cnt += minstep(diff[last+1: n])
        return cnt
