class Solution {
public:
    #define ll long long
    long long minimumOperations(vector<int>& v, vector<int>& z) {
        ll n=v.size();
        vector<ll>d(n+1);
        for(int i=0;i<n;i++){
            d[i] = v[i]-z[i];
        }
        ll ans=0,l=0,c=0;
        ans=abs(d[0]);
        for(int i=1;i<n;i++){
            if(d[i]>0 && d[i-1]>0){
                ans+=max(0ll,d[i]-d[i-1]);
            }else if(d[i]<0 && d[i-1]<0){
                ans+=abs(max(0ll,d[i-1]-d[i]));
            }else if((d[i]>0 && d[i-1]<0) || (d[i]<0 && d[i-1]>0)){
                ans+=abs(d[i]);
            }else if(d[i-1]==0){
                ans+=abs(d[i]);
            }
        }
        return ans;
    }
};
