#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& t) {

        ll ans=0,pre=0;

        for(int i=0;i<v.size();i++){
            int x=t[i];
            if(v[i]>x){
                if(pre>0)pre=0;
                if(-pre>v[i]-x){
                    pre=(x-v[i]);
                }
                else{
                    ans+=v[i]-x+pre;
                    pre=x-v[i];
                }
            }
            else if(v[i]<x){
                if(pre<0)pre=0;
                if(pre>x-v[i]){
                    pre=(x-v[i]);
                }
                else{
                    ans+=x-v[i]-pre;
                    pre=x-v[i];
                }
            }
            else{
                pre=0;
            }
            // cout<<ans<<endl;
            // cout<<pre<<endl;
        }
        // cout<<endl;

        return ans;

    }
};
