function minimumOperations(nums: number[], target: number[]): number {
    nums = nums.map((item, index) => item - target[index]);
    const n = nums.length;
    let ans = 0;
    console.log(nums);
    for(let i = 1, arr = [nums[0]]; i <= n; i++) {
        if (i === n) {
            ans += rd(arr);
            break;
        }
        if (nums[i] * nums[i - 1] > 0) {
            arr.push(nums[i]);
            continue;
        }
        ans += rd(arr);;
        arr = [nums[i]];
    }
    return ans;
};

function rd(arr: number[]): number {
    return Math.min(calc(arr), calc(arr.reverse()));
}

function calc(arr: number[]): number {
    if (!arr.length) return 0;
    arr = arr.map(i => Math.abs(i));
    let res = arr[0];
    for(let i = 1, val = arr[0]; i <= arr.length - 1; i++) {
        //console.log(val)
        if (arr[i] >= val) {
            res += arr[i] - val;
            val = arr[i];
            continue;
        }
        val = arr[i];
    }
    //console.log(arr);
    //console.log(res);
    return res;
}
