class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [nums[i] - target[i] for i in range(n)]

        pos, neg = 0, 0

        for i in range(1, n):
            if diff[i] > diff[i - 1]:
                pos += diff[i] - diff[i - 1]
            else:
                neg += diff[i - 1] - diff[i]

        if diff[0] > 0:
            pos += diff[0]
        else:
            neg += -diff[0]

        return max(pos, neg)
