class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        long long prev=target[0]-nums[0];
        ans+=abs(prev);
        for(int i=1;i<n;i++){
            long long dif=target[i]-nums[i];
            if(prev!=dif){
                if((prev<0 && dif<0) || (prev>0 && dif>0)){
                    if(abs(dif)<abs(prev)){
                        prev=dif;
                        continue;
                    }
                    long long tem=abs(abs(dif)-abs(prev));
                    ans+=abs(tem);
                }else{
                    ans+=abs(dif);
                }
                prev=dif;
            }
        }
        return ans;
    }
};
