class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> diff;
        for(int i = 0;i<n;i++){
            diff.push_back(target[i] - nums[i]);
        }
        for(int i = 0;i<n;i++){
           if(diff[i] == 0){}
           else{
            if(diff[i] > 0){
                int mini = diff[i], j  = i;
                while(j<n && diff[j] >  0){
                    mini = min(mini, diff[j]);
                    j++;
                }
                for(int  k = i;k<j;k++){
                    diff[k]-=mini;
                }
                ans+=mini;
            }
            else{
                int maxi = diff[i], j = i;
                while(j<n && diff[j] <  0){
                    maxi = max(maxi, diff[j]);
                    j++;
                }
                for(int  k = i;k<j;k++){
                    diff[k]-=maxi;
                }
                ans+= abs(maxi);
            }
            i--;
           }
        }
        return ans;
    }
};

