#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff;
        for(int i=0;i<n;i++) diff.push_back(target[i]-nums[i]);
        ll ans=0;
        int lastDiff=0;
        for(int i=0;i<n;i++){
            if(i==0 || (diff[i]>0 && diff[i-1]>0 && diff[i]-diff[i-1]>0) || (diff[i]<0 && diff[i-1]<0 && diff[i]-diff[i-1]<0)){
                ans+=abs(diff[i]-lastDiff);
            }else if(i>0 && ((diff[i]>0 && diff[i-1]<=0) || (diff[i]<0 && diff[i-1]>=0)) ){
                ans+=abs(diff[i]);
            }
            lastDiff=diff[i];
        }
        return ans;
    }
};
