#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> temp(n);
        for(int i=0;i<n;i++){
            temp[i]=target[i]-nums[i];
            //cout<<temp[i]<<" ";
        }
        //cout<<"\n";
        ll ans=0;
        ll oper=0;
        bool ch=false;
        for(int i=0;i<n;i++){
            //cout<<ans<<" "<<i<<" "<<temp[i]<<"\n";
            if(temp[i]==0){
                ans+=oper;
                oper=0;
                continue;
            }
            else if(temp[i]>0){
                if(ch){
                    ans+=oper;
                    oper=0;  
                    oper=temp[i];
                    ch=false;
                }
                else{
                    if(temp[i]<=oper){
                        ans+=(oper-temp[i]);
                        oper=temp[i];
                        continue;
                    }
                    else{
                        oper+=(temp[i]-oper);
                    }
                }
            }
            else{
                //cout<<i<<"a";
                if(!ch){

                    ans+=oper;
                    //cout<<ans<<"\n";
                    oper=0;
                    oper=abs(temp[i]);
                    ch=true;
                }
                else{
                    if(abs(temp[i])<=oper){
                        ans+=(oper-abs(temp[i]));
                        oper=abs(temp[i]);
                        continue;
                    }
                    else{
                        oper+=(abs(temp[i])-oper);
                    }
                }
            }
        }
        return ans+oper;
    }
};
