class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        int i=0;
        while(i<nums.size()){
            if(nums[i]==target[i]){
                i++;
            }else{
                if(nums[i]<target[i]){ //inc
                    int j=i;
                    int inc = target[i] - nums[i];
                    while(j<nums.size() && nums[j]<target[j]){
                        inc = min(inc,target[j]-nums[j]);
                        j++;
                    }
                    for(int k=i;k<j;k++){
                        nums[k]+=inc;
                    }
                    res+=inc;
                }
                else{ //dec
                    int j=i;
                    int dec = nums[i] - target[i];
                    while(j<nums.size() && nums[j]>target[j]){
                        dec = min(dec,nums[j]-target[j]);
                        j++;
                    }
                    for(int k=i;k<j;k++){
                        nums[k]-=dec;
                    }
                    res+=dec;
                }
            }

        }
        return res;
    }
};
