class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long n = nums.size();
        vector<long>diff(n);
        for(int i = 0; i < n;i++){
            diff[i]=target[i] - nums[i];
        }
       long long ans = abs(diff[0]);


        for (int i = 1; i < n; ++i) {
            if ((diff[i] > 0 && diff[i - 1] > 0) || (diff[i] < 0 && diff[i - 1] < 0)) {

                if (abs(diff[i]) > abs(diff[i - 1])) {
                    ans += abs(diff[i] - diff[i - 1]);
                }
            } else {

                ans += abs(diff[i]);
            }
        }

        return ans;
    }
};
