class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> delta(n);
        for(int i = 0; i < n; i++)
            delta[i] = target[i] - nums[i];
        //每个位置左边的最小正数与最大负数
        vector<long long> dp(n);
        dp[0] = abs(delta[0]);
        for(int i = 1; i < n; i++)
        {
            //同号
            if(delta[i] >= 0 && delta[i - 1] >= 0 || delta[i] <= 0 && delta[i - 1] <= 0)
            {
                if(abs(delta[i]) < abs(delta[i - 1]))
                    dp[i] = dp[i - 1];
                else
                    dp[i] = dp[i - 1] + abs(delta[i] - delta[i - 1]);
            }
            else
            {
                dp[i] = dp[i - 1] + abs(delta[i]);
            }
        }
        return dp[n - 1];
    }
};
