class Solution(object):
    def calculate_diff(self, nums, target):
        diff = []
        for i in range(len(nums)):
            diff.append(target[i] - nums[i])
        return diff
    def minimumOperations(self, nums, target):
        diff = self.calculate_diff(nums, target)

        total_steps = 0
        seg_diff = 0

        for index in range(len(diff)):
            if index == 0 or (diff[index - 1] > 0 and diff[index] > 0) or (diff[index - 1] < 0 and diff[index] < 0):
                extra_steps = abs(diff[index]) - abs(seg_diff)
                if extra_steps > 0:
                    total_steps += extra_steps
                seg_diff = diff[index]
            else:
                total_steps += abs(diff[index])
                seg_diff = diff[index]

        return total_steps
