class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>diff(n);
        for(int i = 0; i < n; i ++)
        {
            diff[i] = target[i] - nums[i];
        }
        long long ans = 0;
        for(int i = 0; i < n; i ++)
        {
            if(diff[i] == 0)
            {
                int j = i + 1;
                while(j < n && (diff[j] == 0))
                    j ++;
                i = j - 1;
            }
            else if(diff[i] > 0)
            {
                int mmin = diff[i];
                int j = i + 1;
                while(j < n && (diff[j] > 0))
                {
                    mmin = min(mmin, diff[j]);
                    j ++;
                }  
                for(int k = i; k < j; k ++)diff[k] -= mmin;
                ans += (long long)mmin;
                i = i - 1;
            }
            else
            {
                int mmax = diff[i];
                int j = i + 1;
                while(j < n && (diff[j] < 0))
                {
                    mmax = max(mmax, diff[j]);
                    j ++;
                }  
                for(int k = i; k < j; k ++)diff[k] -= mmax;
                ans += (long long)(-mmax);
                i = i - 1;
            }
        }
        return ans;
    }
};
