class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int ans = 0;
        vector<pair<int , int>> arr;
        int n = nums.size();
        for(int i =0 ; i < n; i++){
            if(nums[i] > target[i]){
                arr.push_back({1 , nums[i] - target[i]});
            }else if(nums[i] == target[i]){
                arr.push_back({0 , 0});
            }else{
                arr.push_back({-1 , target[i] - nums[i]});
            }
        }
        int d = 0;
        long long int c = 0;
        for(int i =0 ; i < n; i++){
            if(d == arr[i].first){
                if(c < arr[i].second){
                    ans += arr[i].second - c;
                }
                c = arr[i].second;
            }else if(arr[i].first == 0){
                d = 0;
                c = arr[i].second;
            }else{
                d = arr[i].first;
                ans += arr[i].second;
                c = arr[i].second;
            }
        }
        return ans;
    }
};
