impl Solution {
pub fn minimum_operations(mut nums: Vec<i32>, mut target: Vec<i32>) -> i64 { //i64
    for i in 0..nums.len() {
        target[i] -= nums[i];
    }

    let mut prev = 0;
    let mut res: i64 = 0;

    for i in 0..nums.len() {
        let diff = target[i];

        if diff == 0 {
            prev = 0;
            continue;
        }

        if prev == 0 {
            prev = diff;
            res += diff.abs() as i64;
            continue;
        }

        if prev.is_positive() != diff.is_positive() {
            res += diff.abs() as i64;
            prev = diff;
            continue;
        }

        if diff.abs() > prev.abs() {
            res += diff.abs() as i64 - prev.abs() as i64;
        }

        prev = diff;
    }

    res
}
}
