class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long total_operations = 0;
        int current_segment_diff = 0;
        int n = nums.size();

        for (int i = 0; i < n; ++i) {
            int diff = target[i] - nums[i];
            if (i == 0 || (diff > 0 && current_segment_diff > 0) || (diff < 0 && current_segment_diff < 0)) {
                int additional_operations = max(abs(diff) - abs(current_segment_diff), 0);
                total_operations += additional_operations;
                current_segment_diff = diff;
            } else {
                total_operations += abs(diff);
                current_segment_diff = diff;
            }
        }

        return total_operations;
    }
};
