def min_operations(A):
    print(A)
    op = 0
    low = 0
    high = 0
    for x in A:
        if x<low:
            op += abs(x-low)
            low = x
            high =0
        if x>high:
            op += x -high
            high = x
            low = 0

        if x<=0:
            low = x
        if x>=0:
            high = x
    return op



class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        return min_operations([b-a for a,b in zip(nums,target)])
