class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ope = 0;
        vector<int> diff(n+1);
        for(int i=0;i<n;i++){
            diff[i] = (target[i]-nums[i]);
        }
        ope += abs(diff[0]);
        int last = diff[0];
        for(int i=1;i<n;i++){
            if(diff[i] == 0){
                last = 0;
                continue;
            }else if(diff[i] > 0){
                if(last >= 0){
                    if(diff[i] > last){
                        ope += (diff[i] - last);
                    }
                    last = diff[i];
                }else{
                    ope += diff[i];
                    last = diff[i];
                }
            }else if(diff[i] < 0){
                if(last <= 0){
                    if(diff[i] < last){
                        ope += abs(last - diff[i]);
                    }
                    last = diff[i];
                }else{
                    ope += abs(diff[i]);
                    last = diff[i];
                }
            }
        }
        return ope;
    }
};
