#define rep(i,a,b) for(int i=a;i<b;i++)
#define sz(c) (int)(c).size()

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=sz(nums);
        long long res=0, crops=0;
        rep(i,0,n) {
            int df = target[i]-nums[i];
            if(crops==df) continue;
            if(crops==0) {
                res += abs(df);
                crops = df;
            }
            if((df>0 && crops<0) || (crops>0 && df<0)) {
                res += abs(df);
                crops = df;
            }
            if(abs(crops)>abs(df)) {
                crops = df;
            } else {
                res += (abs(df) - abs(crops));
                crops = df;
            }
        }
        return res;
    }
};
