class Solution {
public:
    using ll=long long;
    long long minimumOperations(vector<int>& nums, vector<int>& tar) 
    {
        int n=nums.size();
        vector<int> arr;
        for(int i=0; i<n; i++)
        {
            arr.push_back(nums[i]-tar[i]);
        }

        int i=0;
        ll ans=0;
        while(i<n)
        {
            if(i==0)
            {
                ans+=1LL*abs(arr[i]);
                i++;
            }
            else if(arr[i-1]>=0 and arr[i]>=0)
            {
                if(arr[i]>=arr[i-1])
                {
                    ans+=1LL*(arr[i]-arr[i-1]);
                }
                i++;
            }
            else if(arr[i-1]<=0 and arr[i]<=0)
            {
                if(arr[i]<=arr[i-1])
                {
                    ans+=1LL*(arr[i-1]-arr[i]);
                }
                i++;
            }
            else
            {
                ans+=1LL*abs(arr[i]);
                i++;
            }
        }

        return ans;
    }
};
