class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long result = 0;
        for (int i = 0; i < n; i++) {
             if (nums[i] > target[i]) {
                int delta = (nums[i] - target[i]);
                result += delta;

                int currentDelta = delta;
                for (int j = i + 1; j < n; j++) {
                    int t = nums[j] - target[j];
                    if(t <= 0){
                        break;
                    }

                    currentDelta = Math.min(currentDelta, t);
                    nums[j] -= currentDelta;
                }
            } else  if (nums[i] < target[i]) {
                int delta = (target[i] - nums[i]);
                result += delta;

                int currentDelta = delta;
                for (int j = i + 1; j < n; j++) {
                    int t = target[j] - nums[j];
                    if(t <= 0){
                        break;
                    }

                    currentDelta = Math.min(currentDelta, t);
                    nums[j] += currentDelta;
                }
            }
        }
        return result;
    }
}
