class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n = nums.size();

        long long saala = 0;
        long long bsdk = 0;
        long long dallaBKL = 0;
        long long resultHaiBKL = 0;

        int i = 0;

        while (i < n) {
            if (saala == 0) {
                if (nums[i] < target[i]) {
                    saala = 1;
                    bsdk = target[i] - nums[i];
                    dallaBKL += 1;
                } else if (nums[i] > target[i]) {
                    saala = -1;
                    bsdk = nums[i] - target[i];
                    dallaBKL += 1;
                } else {
                    ++i;
                    continue;
                }
            }
             else if(saala!=0) {
                if ((nums[i] < target[i] && saala == 1 && bsdk == target[i] - nums[i]) ||
                    (nums[i] > target[i] && saala == -1 && bsdk == nums[i] - target[i])) {
                    dallaBKL += 1;
                    ++i;
                    continue;
                } 
                else if (nums[i] < target[i] && saala == 1) {
                    if (target[i] - nums[i] > bsdk) {
                        dallaBKL = 1;
                        bsdk = target[i] - nums[i];
                    } else {
                        resultHaiBKL += (bsdk - target[i] + nums[i]);
                        bsdk = target[i] - nums[i];
                    }
                    ++i;
                    continue;
                } 
                else if (nums[i] > target[i] && saala == -1) {
                    if (nums[i] - target[i] > bsdk) {
                        dallaBKL = 1;
                        bsdk = nums[i] - target[i];
                    } else {
                        resultHaiBKL += (bsdk + target[i] - nums[i]);
                        bsdk = nums[i] - target[i];
                    }
                    ++i;
                    continue;
                }
                 else if (nums[i] == target[i]) {
                    resultHaiBKL += bsdk;
                    saala = 0;
                    dallaBKL = 0;
                    bsdk = 0;
                } 
                else {
                    resultHaiBKL += bsdk;
                    if (nums[i] < target[i]) {
                        saala = 1;
                        bsdk = target[i] - nums[i];
                        dallaBKL += 1;


                    } 
                    else {
                        saala = -1;
                        bsdk = nums[i] - target[i];
                        dallaBKL += 1;
                    }
                }
            }
            ++i;
        }

        long long ans = resultHaiBKL + bsdk;

        return ans;
    }
};
