class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>fn(n,0),ft(n,0);
        long long a=0,b=0;
        for(int i=0;i<n-1;i++){
            fn[i+1]=nums[i+1]-nums[i];
            ft[i+1]=target[i+1]-target[i];

        }
        fn[0]=nums[0],ft[0]=target[0];
        for(int i=0;i<n;i++){
            if(fn[i]>ft[i]){
                a+=fn[i]-ft[i];
            }else{
                b+=ft[i]-fn[i];
            }
        }
        //cout<<a<<" "<<b<<endl;
        return max(a,b);

    }
};
