class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        long long ans=0;
        int n=nums.size();
        vector<int>dif(n);
        for(int i=0;i<nums.size();i++){
            dif[i]=nums[i]-t[i];
        }
        int v=0;
        for(int i=0;i<n;i++){
            if(i==0){
                ans+=abs(dif[i]);
            }
            else if((dif[i]<0 && dif[i-1]<0) || (dif[i]>0 && dif[i-1]>0)){
                int d=abs(dif[i])-abs(v);
                if(d>0)ans+=d;
            }
            else{
                ans+=abs(dif[i]);
            }
            v=dif[i];
        }
        return ans;
    }
};
