class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        for (int i = 0; i < nums.size(); i++) {
            diff.push_back(target[i] - nums[i]);
        }
        long long op = abs(diff[0]);
        for (int i = 1; i < diff.size(); i++) {
            if (diff[i] != 0 && diff[i-1] != 0 && diff[i] / abs(diff[i]) != diff[i-1] / abs(diff[i-1]))
                op += abs(diff[i]);
            else
                op += max(0, abs(diff[i]) - abs(diff[i-1]));
        }
        return op;
    }
};
