class Solution {
public:
   #include <vector>
#include <cmath>

bool sign(int a,int b){
    long long val=1LL*a*b;
    return val>0;
}

long long minimumOperations(vector<int>& nums, vector<int>& target) {
      int n = nums.size();
    vector<long long> diff(n);


    for(int i=0;i<n;i++) diff[i]=target[i]-nums[i];

   long long res=0,sum=0;

    for(int i=0;i<n;i++){
        if(i>0 and sign(diff[i],diff[i-1])){
            res+=max(0LL,abs(diff[i])-abs(sum));
        }else{
            res+=abs(diff[i]);

        }
        sum=diff[i];

    }
    return res;

}


