class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>diff(nums.size(),0);
        for(int i=0;i<nums.size();i++){
            diff[i]=target[i]-nums[i];
        }
        bool sign=0;
        long long ans=0,x=0;
        ans=abs(diff[0]);
        x=ans;
        if(diff[0]>=0) sign=1;
        for(int i=1;i<nums.size();i++){

            if(diff[i]>=0 && !sign){
                 sign=1;
                ans+=abs(diff[i]);
                x=abs(diff[i]);
            }
            else if(diff[i]<0 && sign){
                 sign=0;
                ans+=abs(diff[i]);
                x=abs(diff[i]);
            }

            else if(x>=abs(diff[i])){
                x=abs(diff[i]);
            }
            else{
                ans+=abs(diff[i])-x;
                x=abs(diff[i]);
            }
        }
        return ans;
    }
};
