class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> stack;
        long long is_positive = 1, ans = 0;
        stack.push_back(0);
        for(int i=0; i<nums.size(); i++){
            long long tmp = target[i] - nums[i];
            if(tmp > 0){
                nums[i] = 0, target[i] = 0;
                if(stack.size() && tmp > stack.back()) ans += tmp - stack.back();
                while(stack.size() && tmp < stack.back()){
                    stack.pop_back();
                }
                stack.push_back(tmp);
            }
            else{
                while(stack.size()){
                    stack.pop_back();
                }
                stack.push_back(0);
            }
        }
        if(stack.size()){
            stack.pop_back();
        }
        stack.push_back(0);
        for(int i=0; i<nums.size(); i++){
            long long tmp = nums[i] - target[i];
            if(tmp > 0){
                nums[i] = 0, target[i] = 0;
                if(stack.size() && tmp > stack.back()) ans += tmp - stack.back();
                while(stack.size() && tmp < stack.back()){
                    stack.pop_back();
                }
                stack.push_back(tmp);
            }
            else{
                while(stack.size()){
                    stack.pop_back();
                }
                stack.push_back(0);
            }
        }
        return ans;
    }
};
