class Solution {
public:
int max(int a,int b){
    if(a>=b) return a;
    else return b;
}
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long answer=0;
        for(int i=0;i<n;){
        vector<int> diff;
        if(nums[i]>target[i]){
       while( i<n and nums[i]>target[i] ){
        diff.push_back(nums[i]-target[i]);
        i++;
       }}
       else {
       while(i<n and  nums[i]<=target[i] ){
        diff.push_back(target[i]-nums[i]);
        i++;
       }}
       int check=diff[0];
       for(int i=1;i<diff.size();i++){
        check=check+max(diff[i]-diff[i-1],(int)0);
       }
        answer+=check;}


        return answer;
    }
    };
