class Solution {
    public long minimumOperations(int[] nums, int[] target) {
      int n = nums.length;
      long ans = Math.abs(nums[0]-target[0]);
      for(int i =1;i <n; i++){
         int difPrev = target[i-1] - nums[i-1];
         int dif  = target[i] - nums[i];
         if( (dif <0 && difPrev >0) || (dif > 0 && difPrev < 0) ){
            ans += Math.abs(dif);     
         }
        else if( Math.abs(dif) > Math.abs(difPrev)){
            ans += Math.abs(dif) - Math.abs(difPrev);
        }
      }
      return ans;   
    }
}
