class Solution {
public:
    long long minimumOperations(std::vector<int>& nums, std::vector<int>& tar) {
        std::vector<int> differences = calculateDifferences(nums, tar);
        return computeTotalOperations(differences);
    }

private:
    std::vector<int> calculateDifferences(const std::vector<int>& nums, const std::vector<int>& tar) {
        int n = nums.size();
        std::vector<int> differences(n);

        for (int i = 0; i < n; ++i) {
            differences[i] = tar[i] - nums[i];
        }

        return differences;
    }

    long long computeTotalOperations(const std::vector<int>& differences) {
        int n = differences.size();
        long long totalOperations = 0;
        int positiveSequence = 0;
        long long minDifference = std::abs(differences[0]);

        totalOperations += minDifference;
        if (differences[0] >= 0) positiveSequence = 1;

        for (int i = 1; i < n; ++i) {
            totalOperations += handleDifference(differences[i], minDifference, positiveSequence);
        }

        return totalOperations;
    }

    long long handleDifference(int difference, long long& minDifference, int& positiveSequence) {
        long long absValue = std::abs(difference);
        long long operations = 0;

        if ((difference < 0 && positiveSequence == 1) || (difference >= 0 && positiveSequence == 0)) {
            operations += absValue;
            minDifference = absValue;
            positiveSequence = !positiveSequence;
        } else if (minDifference >= absValue) {
            minDifference = absValue;
        } else {
            operations += absValue - minDifference;
            minDifference = absValue;
        }

        return operations;
    }
};
